mapper.select:

select n_num, a_idx, n_title, n_content, n_regDate, n_updDate, n_file
from (
     select /*+index_desc(tbl_board pk_board) */
         rownum rn, n_num, a_idx, n_title, n_content, n_regDate, n_updDate, n_file, n_uuid, n_uploadPath 
     from e_notice_tbl
     where rownum <= #{pageNum} * #{amount} 
      )
where rn > (#{pageNum}-1) * #{amount}
